mapper.select:
select * from(
select * from(
select nvl(storenum, -1) as storenum ,m.userid, userpwd, username, userphone, useremail, userzipcode, useraddr, userdetailaddr, birthday,  to_char(joindate, 'YY/MM/DD HH:MI') as joindate, usertype, userstop, point, to_char(sysdate,'YYYY') - to_char(birthday,'YYYY') +1 as age from member m left join seller s on m.userid=s.userid where userstop='N' and m.userid!='master' order by username asc
)where rownum<=${pageNum}*${onePageRecord} order by username desc
)where rownum<=${lastPageRecord}
						   ${onePageRecord}
order by username asc

select * from(
select * from(
select userid, userpwd, username, userphone, useremail, userzipcode, useraddr, userdetailaddr, birthday,  to_char(joindate, 'YY/MM/DD HH:MI') as joindate, usertype, userstop, point, to_char(sysdate,'YYYY') - to_char(birthday,'YYYY') +1 as age from member where usertype = #{userType} and userstop='N' and userid!='master' order by username asc
)where rownum<=${pageNum}*${onePageRecord} order by username desc
)where rownum<=${lastPageRecord}
						   ${onePageRecord}
order by username asc

select * from(
select * from(
select userid, userpwd, username, userphone, useremail, userzipcode, useraddr, userdetailaddr, birthday,  to_char(joindate, 'YY/MM/DD HH:MI') as joindate, usertype, userstop, point, to_char(sysdate,'YYYY') - to_char(birthday,'YYYY') +1 as age from member where usertype = #{userType} and userstop='Y' and userid!='master' order by username asc
)where rownum<=${pageNum}*${onePageRecord} order by username desc
)where rownum<=${lastPageRecord}
						   ${onePageRecord}
order by username asc

select * from(
select * from(
select s.storenum as storenum, s.sellername as sellername, s.userid as userid, to_char(sysdate,'YYYY') - to_char(birthday,'YYYY') +1 as age, 
s.storeemail, s.storename, storezipcode, storeaddr, storedetailaddr, bank, bankname, bankaccount, regiapproval, 
to_char(regiapprovaldate, 'YYYY/MM/DD') as regiapprovaldate, sellerregiimg , usertype
from member m join seller s on m.userid = s.userid
)where rownum<=${pageNum}*${onePageRecord} order by sellername desc
)where rownum<=${lastPageRecord}
						   ${onePageRecord}
order by sellername asc

select * from(
select * from(

select s.storenum as storenum, s.sellername as sellername, s.userid as userid, to_char(sysdate,'YYYY') - to_char(birthday,'YYYY') +1 as age, 
s.storeemail, s.storename, storezipcode, storeaddr, storedetailaddr, bank, bankname, bankaccount, regiapproval, 
to_char(regiapprovaldate, 'YYYY/MM/DD') as regiapprovaldate, sellerregiimg , usertype
, to_char(exitdate, 'YYYY/MM/DD') as exitdate  
from member m join seller s on m.userid = s.userid join member2 m2 on m.userid = m2.userid
 where usertype = 5

)where rownum<=${pageNum}*${onePageRecord} order by sellername desc
)where rownum<=${lastPageRecord}
						   ${onePageRecord}
order by sellername asc

select userid, username, userphone, useremail, userzipcode, useraddr, 
userdetailaddr, birthday, joindate, usertype, point,userstop from member
where userid=#{param1}

		select * from(
	    select * from(
		  select distinct nvl(d.saleprice,0)saleprice,w.userid, w.productnum as productnum, w.optionnum,nvl(o.optionname,' ')optionname,nvl(o.optionprice,0)optionprice, w.wishnum, w.wishoptionnum, w.wishcode,p.productname, p.productprice, p.thumbimg,p.optionselect, to_char(p.productcontent) as productcontent 
				from wish w  
				left join product p 
				    on p.productnum = w.productnum 
				left join optiontbl o
				    on w.optionnum = o.optionnum
				left join discount d
				    on w.productnum = d.productnum
				where w.userid = #{userid}
				order by w.productnum
		)where rownum<=${pageNum}*${onePageRecord} order by productnum desc
		)where rownum<=${lastPageRecord}
       							  ${onePageRecord}
       	order by productnum	asc

mapper.update:
update member 
set username = #{username}, userid=#{userid}, useremail=#{useremail}, userzipcode=#{userzipcode},
useraddr = #{useraddr}, userdetailaddr = #{userdetailaddr} where userid=#{userid}
